mapper.select:
select free.*,member.original profile
from free,member
where free_num = #{num}
and member.id = #{id}

mapper.update:
update free 
set free_readcount=free_readcount+1
WHERE free_num = #{free_num}

sql.if:
and
(free_subject || free_content) free_subject free_content nick
like #{search_word}

mapper.select:
select count(*)
from free_like
where free_id = #{id}
and free_num = #{num}

mapper.delete:
 		delete from free_like
		where free_num=#{num}
		and free_id = #{id}

mapper.update:
update free 
set free_like=free_like+1
where free_num = #{num}

 			update free 
			set free_like=free_like-1
			where free_num = #{num}

mapper.select:
select free_file
from free_files
where free_num=#{num}
