mapper.select:
select count(*)
from jik_like
where jik_id = #{id}
and jik_num = #{num}

mapper.delete:
 		delete from jik_like
		where jik_num=#{num}
		and jik_id = #{id}

mapper.update:
update jik 
set jik_like=jik_like+1
where jik_num = #{num}

