mapper.select:
select count(*)
from jik_comm
where jik_board_num = #{jik_num}

select *
from (select rownum rnum, jik_comm_num, id, nick, jik_comm_content, jik_comm_date,jik_comm_re_ref,jik_comm_re_lev,jik_comm_re_seq,jik_comm_secret
	  from(
	  		select jik_comm_num, id, nick, jik_comm_content, jik_comm_date,jik_comm_re_ref,jik_comm_re_lev,jik_comm_re_seq,jik_comm_secret
	  		from jik_comm
	  		where jik_board_num = #{jik_num}

			order by jik_comm_re_ref 

					desc
					asc

			, jik_comm_re_seq asc

	  		)
	  )
where rnum between #{start} and #{end}

mapper.update:
update jik_comm
set jik_comm_content = #{jik_comm_content}
where jik_comm_num= #{jik_comm_num}

update jik_comm
set jik_comm_re_seq=jik_comm_re_seq+1
where jik_comm_re_ref=#{jik_comm_re_seq}
and jik_comm_re_seq  >  #{jik_comm_re_seq}
