mapper.select:
select ID, NICK
 from ( select rownum rnum, b.*
         from ( select *
                from member
                where MYNONG_NAME = #{mynong_name} and MY_FARM = '2'
                order by id
               )b
           )
       where rnum >= #{start} and rnum <= #{end}

select MY_FARM
from member
where id = #{id}

select MYNONG_NAME
from member
where id = #{id}
